mapper.select:
SELECT bno, ref, step, depth, title, content, writer, regdate, blind_yn
FROM tbl_board
where bno = #{bno}

mapper.update:
UPDATE tbl_board
SET title =#{title}, content =#{content}
WHERE bno = #{bno}
AND writer = #{writer}


UPDATE TBL_BOARD SET depth = depth + 1
WHERE ref = #{ref}
AND depth >= #{depth};
